import React, { useEffect, useState } from 'react';
import './App.css';
import db from './firebase';
import firebase from 'firebase';

function  App() {
    const [input, setInput] = useState('');
    const [userinfo, setUserinfo] = useState([]);
    const [user, setUser] = useState('');

    // useEffect(()=>{
    //     const name = prompt('please, enter your name');
    //     checkName(name);
    // },[])

    // const checkName = (name)=>{
    //     if(name === ''){
    //         const user_name = prompt('You did not write your Name');
    //         if(user_name === ''){
    //             checkName(name);
    //         }else{
    //             setName(user_name);
    //         }
    //     }else{
    //         setName(name);
    //     }
    // }
    useEffect(() => {
        let userName = prompt('Please, write your name !');
        checkUserName(userName);
    }, [])

    const checkUserName = (user) => {
        if (!user) {
            let n = prompt('You did not write your Name');
            if (n) {
                setUser(n)
            } else {
                checkUserName(user);
            }
        } else {
            setUser(user)
        }
    }
    const handleInput = (e)=>{
        setInput(e.target.value);
    }
    const handleButton = (e)=>{
        e.preventDefault();
        db.collection('messages').add({
            message: input,
            timestamp: firebase.firestore.FieldValue.serverTimestamp(),
            username: user
        })
        setUserinfo([...userinfo, {
            message: input,
        }])
        setInput('');
    }
    return(
        <div>
            <form>
                <input value={input} type="text" onChange={handleInput}/>
                <button onClick={handleButton}>Send</button>
            </form>
        </div>
    )
}
export default App;
