import React, { useEffect, useState } from 'react';
import Message from './components/Message';
import db from './firebase';
import firebase from 'firebase';
import FlipMove from 'react-flip-move';
import './App.css';

function App() {
    const [input, setInput] = useState('');
    const [userInfos, setUserInfos] = useState([]);
    const [user, setUser] = useState('');
    useEffect(() => {
        db.collection('messages')
            .orderBy('timestamp', 'desc')
            .onSnapshot((snapshot) => {
                setUserInfos(snapshot.docs.map(doc => (
                    {data:doc.data(),id:doc.id}
                )))
            })
    }, [])
    useEffect(() => {
        let userName = prompt('Please, write your name !');
        checkUserName(userName);
    }, [])

    const checkUserName = (user) => {
        if (!user) {
            let n = prompt('You did not write your Name');
            if (n) {
                setUser(n)
            } else {
                checkUserName(user);
            }
        } else {
            setUser(user)
        }
    }
    const handleInput = (e) => {
        setInput(e.target.value);
    }
    const handleButton = (e) => {
        e.preventDefault();
        db.collection('messages').add({
            message: input,
            username: user,
            timestamp: firebase.firestore.FieldValue.serverTimestamp()
        })
        setInput('');
    }
    return (
        <div className='container'>
            <form>
                <input type="text" value={input} onChange={handleInput} />
                <button type='submit' onClick={handleButton}>Add Todos</button>
            </form>
            <FlipMove>
                {
                    userInfos.map((userInfo) => (
                        <Message key={userInfo.id} userInfo={userInfo.data} userName={user} />
                    ))
                }
            </FlipMove>
        </div>
    )
}
export default App;
