import React, { useEffect, useState } from 'react';
import './App.css';
import db from './firebase';
import firebase from 'firebase';
import Message from './components/Message';

function  App() {
    const [input, setInput] = useState('');
    const [userinfo, setUserinfo] = useState([]);
    const [name, setName] = useState('');
    useEffect(()=>{
        db.collection('messages').onSnapshot((snapshot)=>{
            setUserinfo(snapshot.docs.map((doc)=>(
                {
                    data: doc.data(),
                    id: doc.id
                }
            )))
        })
    },[])
    useEffect(()=>{
        const name = prompt('please, enter your name');
        checkName(name);
    },[])

    const checkName = (name)=>{
        if(!name){
            const user_name = prompt('You did not write your Name');
            if(!user_name){
                checkName(name);
            }else{
                setName(user_name);
            }
        }else{
            setName(name);
        }
    }
    const handleInput = (e)=>{
        setInput(e.target.value);
    }
    const handleButton = (e)=>{
        e.preventDefault();
        db.collection('messages').add({
            message: input,
            timestamp: firebase.firestore.FieldValue.serverTimestamp(),
            username: name
        })
        setInput('');
    }
    return(
        <div className='wrapper'>
            <form>
                <input placeholder='Enter a message...' value={input} type="text" onChange={handleInput}/>
                <div className="button_con">
                    <button onClick={handleButton}><i class="fas fa-paper-plane"></i></button>
                </div>
            </form>
            {
                userinfo.map((doc)=>(
                    <Message data={doc.data} key={doc.id}/>
                ))
            }
        </div>
    )
}
export default App;
