import React from 'react';
import './Message.css';
import db from '../firebase';

function  Message({data,id}) {
    const delMessage = (e)=>{
        db.collection('messages').doc(id).delete();
    }
    const editandSaveMessage = (e)=>{
        let ele = e.target;
        let node = e.target.parentElement.children;
        console.log(e.target.parentElement.children);
        const editMessage = ()=>{
            let p = node[0];
            p.contentEditable = true;
            p.focus();
            ele.textContent = 'Save';
        }
        const saveDB = ()=>{
            db.collection('message').doc(id).set({
                message: node[0].value
            },{merge: true});
            ele.textContent = 'Edit';
        }
        if(ele.textContent === 'Edit'){
            editMessage()
        }else if(ele.textContent === 'Save'){
            saveDB()
        }
    }
    
    return (
        <div className='mess_con'>
            <p className='mess_text'>{data.username} : {data.message}</p>
            <button className='mess_edit' onClick={editandSaveMessage}>Edit</button>
            <button className='mess_delete' onClick={delMessage}>del</button>
        </div>
    )
}
export default Message;
