import React from 'react';
import './Message.css';
import db from '../firebase';

function  Message({data,id}) {
    const delMessage = (e)=>{
        console.log(e.target.parentElement.children);
        db.collection('messages').doc(id).delete();
    }
    const editandSaveMessage = (e)=>{
        let par = consoele.log(e.target.value);
        const editMessage = (e)=>{
            let p = e.target.parentElement.children[0];
            p.contentEditable = true;
            p.focus();
            p.textContent = 'Save';
            // db.collection('messages').doc(id).set({
            //     message: p.value,
            // },{merge: true})
        }
    }
    
    return (
        <div className='mess_con'>
            <p className='mess_text'>{data.username} : {data.message}</p>
            <button className='mess_edit' onClick={editandSaveMessage}>Edi</button>
            <button className='mess_delete' onClick={delMessage}>del</button>
        </div>
    )
}
export default Message;
