import React from 'react';
import './Message.css';

function Message({userInfo, userName}){
    let balance = (userName === userInfo.user) ? 'realUser':'guestUser';
    let leftRigth = (userName === userInfo.user) ? 'real_wrap':'guest_wrap';
    return (
        <div className={`message_wrap ${leftRigth}`}>
            <p className={`user_message ${balance}`}>{userInfo.user } : {userInfo.text}</p>
        </div>
    )
}
export default Message;
