import React from 'react';
import './Message.css';

const Message = React.forwardRef(({userInfo, userName},ref)=>{
    let balance = (userName === userInfo.username) ? 'realUser':'guestUser';
    let leftRigth = (userName === userInfo.username) ? 'real_wrap':'guest_wrap';
    let owner = (userName === userInfo.username)
    return (
        <div ref={ref} className={`message_wrap ${leftRigth}`}>
            <p className={`user_message ${balance}`}>{!owner && userInfo.username} {userInfo.message}</p>
        </div>
    )
})
export default Message;
